function hattip_die() {
  $("#hattip").removeClass("fire loud"),
    setTimeout(() => {
      $("#hattip #msg").html(""), $("#hattip").removeAttr("class");
    }, 400);
}
function hattip_fire(t = "Hello World!", a = "ht-default", i = "keep-calm") {
  let e = 50;
  $("#hattip").data("loud", i),
    $("#hattip").hasClass("fire") && (hattip_die(), (e = 500)),
    setTimeout(() => {
      $("#hattip #msg").html(t),
        $("#hattip").addClass(a).addClass("display"),
        setTimeout(() => {
          "dont-panic" == $("#hattip").data("loud")
            ? $("#hattip").addClass("fire loud")
            : $("#hattip").addClass("fire");
        }, 30);
    }, e);
}
$(document).ready(() => {
  $("#hattip button").click(() => {
    hattip_die();
  });
});
